// the global variable
var globalTitle = "Winter Is Coming";

// Add your code below this line
function urlSlug(title) {
  //returns hyphenated version of title
  //trims whitespaces in beginning and end of string
  title = title.trim();
  //split the string into an array of strings using whitespace as delimeter
  title = title.split(/\s+/);
  //and finally join the string array using hyphen as a delimter then lower casing everything
  return title.join("-").toLowerCase();
}
