function diffArray(arr1, arr2) {
  //return this newArr with any items only found in one of the two given arrays
  var newArr = [];
  let isUnique = true;

  //eval first arr to 2nd
  for(var i=0;i<arr1.length;i++){
    for(var j=0;j<arr2.length;j++){
      //compare if the value at arr[i] is inside array in 2
      if(arr1[i] == arr2[j]){
        //console.log("arr1: " + arr1[i] + " arr2: " + arr2[j])
        isUnique = false;
      }
    }
    //if the array is uniqe then push the value to the newArr
    if(isUnique){
      newArr.push(arr1[i]);
    }
    isUnique = true; 
  }

  //eval 2nd arr to 1st
  for(var i=0;i<arr2.length;i++){
    for(var j=0;j<arr1.length;j++){
      //compare if the value at arr[i] is inside array in 2
      if(arr2[i] == arr1[j]){
        //console.log("arr1: " + arr1[i] + " arr2: " + arr2[j])
        isUnique = false;
      }
    }
    //if the array is uniqe then push the value to the newArr
    if(isUnique){
      newArr.push(arr2[i]);
    }
    isUnique = true; 
  }
  // Same, same; but different.
  return newArr;
}

console.log(diffArray([1, 2, 3, 5], [1, 2, 3, 4, 5]));
