function mutation(arr) {
  //return true if the string in the first element of the array contains all of the letters in 2nd arr
  arr[0] = arr[0].toLowerCase();
  arr[1] = arr[1].toLowerCase();
  let counter =0;
  //this loop will run arr[1].length times
  for(var i=0;i<arr[1].length;i++){
    //this loop runs arr[0].length times
    for(var j=0;j<arr[0].length;j++){
      //now compare the value at index i on 2nd string to each char in 1st string-*one thing to note is that I had to use 'break' because my counter would be take account multiple characters being evaluated more than once-*
      if(arr[1].charAt(i) == arr[0].charAt(j)) {
        counter++;
        break;
      }
    }

  }
  console.log(counter);
  return (counter == arr[1].length) ? true : false;
}

console.log(mutation(["hello", "hey"]));
console.log(mutation(["hello", "hey"]));
console.log(mutation(["hello", "Hello"]));
