function destroyer(arr) {
  // Remove all the values

  //initial for loop, loopoing "arguments" times 
  for(var i=1;i<arguments.length;i++){
    for(var j=0;j<arr.length;j++){

      if(arguments[i] == arr[j]){
        console.log("j: " + j);
        console.log("i: " + arguments[i] + " j: " + arr[j]);
        console.log("actual j: " + j);
        arr.splice(j,1);
        console.log("arr: " + arr);
        console.log("arrlength: " + arr.length);
        j = -1;
      }
    } 
  }

  return arr;
}
