function sumAll(arr) {
  //return sum of 2 numbers plus the sum of all numbers in between them
  if(arr[0] > arr[1]){
    let temp = arr[1];
    arr[1] = arr[0];
    arr[0] = temp;
  }
  //first sum of 2 numbers
  let x = arr[0] + arr[1];
  //now sum all nums in between these 2 numbers
  let sum = 0;
  for(var i=arr[0]+1;i<arr[1];i++)  sum+= i;
  return x + sum;
}

console.log(sumAll([1, 4]));
console.log(sumAll([4, 1]));
