function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
  //sort array first
  let temp;
  function sort(){
    //console.log('testing: ');
    for(var i=1;i<arr.length;i++){
    if(arr[i-1]> arr[i]){
      temp = arr[i];
      arr[i] = arr[i-1];
      arr[i-1] = temp;
      }
    }
  }
  function notSorted(){
    for(var i = 1;i<arr.length;i++){
      if(arr[i-1] > arr[i]) return true;
    }
    return false;
  }
  //sort it for me
  while( notSorted() ){
    sort();
  }

  if(arr.includes(num)){
    for(var i=0;i<arr.length;i++){
      if(arr[i] == num) return i;
    }
  }
  //now insert at index which it should be inserted if sorted
  for(var i=1;i<arr.length;i++){
    //for when a num is in between two values
    if(num > arr[i-1] && num < arr[i]) return i;
  }

  return arr.length;

}
